<script>
import MobileAlt from '@@/../components/icons/MobileAlt.vue'
import DesktopAlt from '@@/../components/icons/DesktopAlt.vue'
import ChevronRight from '@@/../components/icons/ChevronRight.vue'
import CircleAlt from '@@/../components/icons/CircleAlt.vue'
import i18n from '@@/lang/components-mobile/footer.json'
import { setCookie } from '@/utils/client/common'
import Lzimg from '@@/../components/Lzimg.vue'
import ChangeLang from "@/components/user/ChangeLang.vue"
import LazyComp01 from '@@/../components/LazyComp01.vue'
import Twitter from '@@/../components/icons/Twitter.vue'
import FacebookSquare from '@@/../components/icons/FacebookSquare.vue'
import LineIcon from '@@/../components/icons/Line.vue'
import YoutubeIcon from '@@/../components/icons/YoutubeIcon.vue'
import Instagram from '@@/../components/icons/Instagram.vue'

export default {
  components: {
    MobileAlt,
    DesktopAlt,
    ChevronRight,
    CircleAlt,
    Lzimg,
    ChangeLang,
    LazyComp01,
    FacebookSquare,
    Twitter,
    LineIcon,
    Instagram,
    YoutubeIcon,
  },
  i18n: { messages: i18n },
  methods: {
    switchToDesktop() {
      setCookie('forceRenderDesktop', true, 30 * 24 * 60)
      $('.loading-mobile').show()
      let vp = document.querySelector('meta[name="viewport"]')
      vp.setAttribute(
        'content',
        'width=device-width, maximum-scale=' + window.screen.availWidth / 1025
      )
      // TODO: Hieu Nguyen - Correct finance navi structure
      if (this.$route.path.includes('/t/')) {
        location.href = this.$route.path.replace(
          '/searchresult/',
          '/articles/searchresult/'
        )
      } else {
        location.reload()
      }
    },
  },
  computed: {
    supportLocale() {
      return this.langSupported().includes(this.$i18n.locale)
    }
  }
}
</script>

